import React, { useState } from 'react'
import Kcreate from './Kcreate'
import Kshow from './Kshow'

function Knote(){
    const [item,setitem]=useState([])
    const addnote=(note)=>{
        setitem((olddate)=>{
            return[...olddate,note]
        })
        console.log(note)
    }
    return(
        <>
        <Kcreate passnote={addnote} />
        <Kshow/>
        </>
    )
}

export default Knote;
