import React from "react";

const App = () => {
  const style = {
    width: "100vw",
    height: "100vh",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "pink",
    color: "white",
  };
  const input = {
    width: "200px",
    height: "40px",
    border: "none",
    backgroundColor: "none",
    margin: "10px",
    textAlign: "center",
    outline: "none",
    borderRadius: "15px",
  };
  const button = {
    width: "80px",
    height: "38px",
    backgroundColor: "none",
    border: "none",
    outline: "none",
    borderRadius: "10px",
  };
  return (
    <>
      <form>
        <div style={style}>
          <h1>HEY HARI</h1>
          <input
            style={input}
            type="text"
            placeholder="enter your god name"
          ></input>
          <input
            style={input}
            type="text"
            placeholder="enter your god name"
          ></input>
          <button style={button} type="submit">
            submit
          </button>
        </div>
      </form>
    </>
  );
};

export default App;
