import React, { useState } from 'react'
import Kcreate from './Kcreate'
import Kshow from './Kshow'

function Knote(){
    const [item,setitem]=useState([])
    const addnote=(note)=>{
        setitem((olddate)=>{
            return[...olddate,note]
        })
        console.log(note)
    }
    const deleteo=(id)=>{
        setitem((olddata)=>{
            olddata.filter((val,index)=>{
                return index !== id;
            })
        })
    }
    return(
        <>
        <Kcreate passnote={addnote} />
        {item.map((val,index)=>{
            return <Kshow 
                key={index}
                id={index}
                title={val.title}
                note={val.note}
                ondelete={deleteo}
            />
        })}
        </>
    )
}

export default Knote;
