import React, { useState } from "react";
const button={background:'red',color:'white',height:'50px',width:'150px',borderRadius:'5px',border:'none',outline:'none'}
function App() {
  const [ccount,setcount]=useState('SHREE HARI');
  const [bg,setbg]=useState('black')
  const style={display:'flex',justifyContent:'center',alignItems:'center', width:'100vw',height:'100vh',backgroundColor:'bg'}
  function updatetime(){
    setbg('green')
    setcount('JAY SWAMINARAYAN');
  }
  return (
    <>
    <div style={style}>
      <button style={button} onClick={updatetime}>{ccount}</button>
    </div>
    </>
  );
}

export default App;
