    borderRadius: "5px",
    border: "none",
    outline: "none",
    display:'block'
  };
  const style = {
    display: "flex",
    justifyContent: "center",
    // alignItems: "center",
    width: "100vw",
    height: "100vh",
    margin:'2rem auto'
    // backgroundColor: bg,
  };
  return (
    <>
      <div style={style}>
        <h1>hello HARI</h1>
        <input type="text"></input>
        <button style={button} >submit</button>
      </div>
    </>
  );
}

export default App;
