import React, { useState } from "react";
import "./index.css";
import Call from './Call'
const Note = () => {

    const [name,setname]=useState('')
    const [btn,setbtn]=useState([])
    const input=(e)=>{
        setname(e.target.value)
    }
    const click=(oldvalues)=>{
        setbtn([...oldvalues,name])
    }
  return (
    <>
      <div className="container">
        <div id="card">
          <h1>add hari</h1>
          <input onChange={input} placeholder="enter hari's name"></input>
          <button onClick={click}>+</button>
            <div id='output'>
                <button >-</button>
                <li>{btn}</li>
            </div>
        </div>
      </div>
    </>
  );
};

export default Note;
