import React, { useEffect, useState } from "react";

function Effect() {
    const [name,setname]=useState(0)
    useEffect(()=>{alert('i am clicked')},[])
  return (
    <>
      <h1>hello {name}</h1>
      <button onClick={()=>{
          setname(name+1);
      }}></button>
    </>
  );
}

export default Effect;
