import React, { useState } from "react";

function App() {
  const button = {
    background: "red",
    color: "white",
    height: "50px",
    width: "150px",
    borderRadius: "5px",
    border: "none",
    outline: "none",
    display: "block",
  };
  const style = {
    display: "flex",
    flexDirection:'column',
    justifyContent: "center",
    alignItems: "center",
    width: "100vw",
    height: "100vh",
    margin: "10px",
    // backgroundColor: bg,
  };
  const input={
    height:'30px',
    width:'200px',
    margin:'10px'
  }
  const [name,setname]=useState('')
  function inputevent(e){
    setname=e.target.value;
  }
  return (
    <>
      <div style={style}>
          <h1>HELLO HARI {name}</h1>
          <input onInput={inputevent} style={input} placeholder="enter your god name" value={name} type="text"></input>
          <button style={button}>SUBMIT</button>
      </div>
    </>
  );
}

export default App;
