import React, { useState } from "react";
import AddIcon from '@material-ui/icons/Add';
import "./index2.css";

const Incredecre = () => {
  const [item, setitem] = useState(0);
  const incre=()=>{
      setitem(item+1);
  }
  const decre=()=>{
      if(item>0){
          setitem(item-1);
      }else{
          alert('MAXIMUM DECREMENT REACHED')
      }
  }
  return (
    <>
      <div className="container">
        <div id="card">
          <h1>{item}</h1>
          <button onClick={incre}><AddIcon/></button>
          <button onClick={decre}>Decre</button>
        </div>
      </div>
    </>
  );
};

export default Incredecre;
