import React, { useState } from "react";
import Kcreate from "./Kcreate";
import Kshow from "./Kshow";

function Knote() {
  const [additem, setitem] = useState([]);
  const addnote = (note) => {
    setitem((olddate) => {
      return [...olddate, note];
    });
    console.log(note);
  };
  const deleteo = (id) => {
    //   alert('boom')
      setitem((prevdata)=>{
          prevdata.filter((val,index)=>{
              return index!==id;
          })
      })
  };
  return (
    <>
      <Kcreate passnote={addnote} />
      {additem.map((val, index) => {
        return (
          <Kshow
            key={index}
            id={index}
            title={val.title}
            note={val.note}
            ondelete={deleteo}
          />
        );
      })}
    </>
  );
}

export default Knote;
