import React, { useCallback, useState } from "react";

function App() {
  const button = {
    background: "red",
    color: "white",
    height: "50px",
    width: "150px",
    borderRadius: "5px",
    border: "none",
    outline: "none",
    display: "block",
  };
  const style = {
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
    width: "100vw",
    height: "100vh",
    margin: "10px",
    // backgroundColor: bg,
  };
  const input = {
    height: "30px",
    width: "200px",
    margin: "10px",
  };
  const [name, setname] = useState("");
  const [fullname, setfullname] = useState("");
  function inputevent(e) {
    console.log(e.target.value);
    setname(e.target.value);
  }
  function click(e) {
    console.log("clicked");
    setfullname(name);
  }
  return (
    <>
      <div style={style}>
        <h1>HELLO HARI {fullname}</h1>
        <input
          onChange={inputevent}
          style={input}
          placeholder="enter your god name"
          value={name}
          type="text"
        ></input>
        <button onClick={click} style={button}>
          SUBMIT
        </button>
      </div>
    </>
  );
}

export default App;
